sys_script_include.script:
var MapGrid = Class.create();
MapGrid.prototype = {

      initialize: function(width, height) {
        this.width = width;
        this.height = height;
        this.cells = [];

        for (var y = 0; y < height; y++) {
            for (var x = 0; x < width; x++) {
                this.cells.push({
                    x: x,
                    y: y,
					selected: false,
					bg_img: "x_91469_map_wizard.black_bg.png",
					border_c: "#0000FF",
					id: 'mp_' + x + "_" + y
                });
            }
        }
    },

    getCell: function(x, y) {
        return this.cells.find(function(cell) {
            return cell.x === x && cell.y === y;
        });
    },

    setCellValue: function(x, y, att, val) {
        var cell = this.getCell(x, y);
        if (cell) {
            cell[att] = val;
        }
        return cell;
    },


    type: 'MapGrid'
};
